publishing.custom_css:
/* you can write your own CSS here that will be applied to all the space public pages */

body.fluid .scope_title {
  text-align: inherit;
}

body.islam {
  direction:rtl;
}

body.islam .content {
  margin-right: 270px;
  margin-left: 0;
}
body.islam .sidebar {
  left: auto;
  right: 0;
}

body.islam blockquote {
  border-left: none;
  border-right: 4px solid #DDD;
  font-size: 125%;
  line-height: 1.6;
}

settings.custom_css:
body {
  color: #222;
  margin: 0;
  padding: 0 10px;
  /*text-shadow: 0 1px 0 #fff;*/
  background: #FFF;
  font-family: Arial;
}

h1,h2,h3,h4,h5,h6 { font-family: inherit; color: #555; }

h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {
  text-decoration: none;
}

a, header h1 a { color: #CC0000; }
a { text-decoration: underline; }
a:visited { color: #771100; }
a:hover:not(.selected)   { color: #DD7700; }
.selected { color: #222; text-decoration: none; }

nav {
  border-bottom: 1px solid #ddd;
  margin: 0 0 10px 0;
  padding: 10px 0;
}

#breadcrumbs {
  background: #eee;
  margin: 10px 0;
  font-weight: bold;
  letter-spacing: 1px;
  font-size: 0.8em;
}

#breadcrumbs > a {
  padding-top: 5px; padding-bottom: 5px;
}

.stuff    { width: 960px; margin:0 auto;}
.with_sidebar #content  { width: 720px; max-width: inherit; }
#sidebar  { width: 225px; }
#sidebar,
#content {
  clear: none;
  display: inline-block;
  vertical-align: top;
}


#sidebar {
  margin-left: 10px;
  opacity: 0.85;
  background: #eee;
  font-size: 0.9em;
  padding: 10px;
}
#sidebar #group_listing > h2:first-child { margin-left: 0; background: #ccc; }
#sidebar h2 { width: 225px; position: relative; left: -10px; padding: 10px;}
